mapper.select:
SELECT STOP_DATE
FROM VIEW_MEM_STOP
WHERE ID = #{id}

SELECT MEM_ID
FROM TBL_MEM_JOIN
WHERE NAME=#{name}
AND TEL=#{tel}

{ CALL PRC_MEM_JOIN
   (
      #{id}
      , #{name}
      , #{email}
      , #{tel}
      , #{pwd}
      , #{idntt_cd}
      , #{img_url, jdbcType=VARCHAR}
      , #{mem_content, jdbcType=VARCHAR}
      , #{spc_area_cd}
      , #{mem_cd, mode=OUT, jdbcType=VARCHAR}
   )
}

mapper.update:
UPDATE TBL_MEM_JOIN
SET PWD=CRYPTPACK.ENCRYPT(#{pwd},#{pwd})
WHERE NAME=#{name}
AND MEM_ID=#{id}
AND TEL=#{tel}

mapper.select:
SELECT COUNT(*) AS COUNT
FROM TBL_MEM_JOIN
WHERE MEM_ID = #{id}
AND NAME=#{name}
AND TEL=#{tel}

SELECT COUNT(*) AS COUNT
FROM TBL_MEM_JOIN
WHERE MEM_ID = #{id}

SELECT COUNT(*) AS COUNT
FROM TBL_MEM_JOIN
WHERE TEL = #{tel}

SELECT COUNT(*) AS COUNT
FROM TBL_MEM_INT_ETC_TAG
WHERE ETC_TAG=#{etc_tag}


      	SELECT COUNT(*) AS PNLT_CNT
		FROM TBL_MEM_PNLT
		WHERE MEM_CD = #{mem_cd}

SELECT NAME, IDNTT, MEM_CONTENT
FROM VIEW_MEM_MY
WHERE MEM_CD = #{mem_cd}
