mapper.select:
SELECT STOP_DATE
FROM VIEW_SPA_STOP
WHERE ID = #{id}

	SELECT SPA_ID
	FROM TBL_SPA_JOIN
	WHERE NAME=#{name}
  	  AND TEL=#{tel}

mapper.update:
		UPDATE TBL_SPA_JOIN
		SET PWD=CRYPTPACK.ENCRYPT(#{pwd},#{pwd})
		WHERE NAME=#{name}
  		  AND SPA_ID=#{spa_id}
  		  AND TEL=#{tel}

mapper.select:
SELECT COUNT(*) AS COUNT
FROM TBL_SPA_JOIN
WHERE SPA_ID = #{spa_id}
  AND NAME=#{name}
  AND TEL=#{tel}

SELECT COUNT(*) AS COUNT
FROM TBL_SPA_JOIN
WHERE SPA_ID = #{spa_id}

SELECT COUNT(*) AS COUNT
FROM TBL_SPA_JOIN
WHERE TEL = #{tel}

SELECT SPA_CD
FROM TBL_SPA_JOIN
WHERE SPA_ID=#{spa_id}
