SELECT INT_TAG AS INT_TAG_NAME
FROM VIEW_STU_TAG
WHERE STU_CD = #{stu_cd}

SELECT ETC_TAG AS ETC_TAG_NAME
FROM VIEW_STU_ETC_TAG
WHERE STU_CD = #{stu_cd}

		SELECT DISTINCT LEADER_NAME, LEADER_MEM_CD
		FROM VIEW_STU_INFO
