mapper.delete:
SELECT STU_CD, STU_NTC_CD, STU_NTC_PNT_CD, STU_JOIN_CD, TITLE
     , CRT_DATE, CONTENT, HITS, DEL_DATE
FROM VIEW_STU_NTC
WHERE STU_CD = #{stu_cd}
  AND STU_NTC_PNT_CD = #{stu_ntc_pnt_cd}
  AND STU_NTC_PNT_CD != STU_NTC_CD
  AND STU_NTC_CD = #{stu_ntc_cd}

mapper.select:
SELECT STU_CD, STU_NTC_CD, STU_NTC_PNT_CD, STU_JOIN_CD, TITLE
     , CRT_DATE, CONTENT, HITS, DEL_DATE
FROM VIEW_STU_NTC
WHERE STU_CD = #{stu_cd}
  AND STU_NTC_PNT_CD = #{stu_ntc_pnt_cd}
  AND STU_NTC_PNT_CD = STU_NTC_CD

SELECT STU_CD, STU_NTC_CD, STU_NTC_PNT_CD, STU_JOIN_CD, TITLE
     , CRT_DATE, CONTENT, HITS, DEL_DATE
FROM VIEW_STU_NTC
WHERE STU_CD = #{stu_cd}
  AND STU_NTC_PNT_CD = #{stu_ntc_pnt_cd}
  AND STU_NTC_PNT_CD != STU_NTC_CD

SELECT COUNT(*) AS COUNT
FROM VIEW_STU_NTC
WHERE STU_CD = #{stu_cd}
  AND STU_NTC_PNT_CD = #{stu_ntc_pnt_cd}
  AND STU_NTC_PNT_CD != STU_NTC_CD
